<script>
import Detail from "./detail/Detail.vue"
import LineCharts from "./linecharts/LineCharts.vue"
import BarCharts from "./barcharts/BarCharts.vue"
import ProgressCharts from "./progresscharts/ProgressCharts.vue"
import { mapState } from "vuex"
export default {
    name: "Card",
    components: { Detail, LineCharts, BarCharts, ProgressCharts },
    data() {
        return {
            saleMonthUpOrDown: false,
            saleDayUpOrDown: false,
            activityMonthUpOrDown: false,
            activityDayUpOrDown: false,
        }
    },
    computed: {
        ...mapState({
            listState: (state) => {
                return state.home.list;
            }
        }),
        // 总销售额度
        salesTotal() {
            return this.listState.salesTotal;
        },
        // 日销售额
        salesToday() {
            return this.listState.salesToday;
        },
        // 月同比增长
        salesGrowthLastMonth() {
            let number = this.listState.salesGrowthLastMonth
            if (number > 0) {
                this.saleMonthUpOrDown = true;
                return number;
            } else {
                this.saleMonthUpOrDown = false;
                return number;
            }
        },
        // 日同比增长
        salesGrowthLastDay() {
            let number = this.listState.salesGrowthLastDay
            if (number >= 0) {
                this.saleDayUpOrDown = true;
                return number;
            } else {
                this.saleDayUpOrDown = false;
                return number;
            }
        },
        // 总访问量
        visitTotal() {
            return this.listState.visitTotal;
        },
        // 日访问量
        visitToday() {
            return this.listState.visitToday;
        },
        // 访问趋势数据
        visitTrend() {
            return this.listState.visitTrend;
        },
        // 支付数量
        payTotal() {
            return this.listState.payTotal;
        },
        // 转化率
        payRate() {
            return this.listState.payRate;
        },
        // 支付趋势数据
        payTrend() {
            return this.listState.payTrend;
        },
        // 运营效果月同比
        activityGrowthLastMonth() {
            let number = this.listState.activityGrowthLastMonth;
            this.activityMonthUpOrDown = number >= 0 ? true : false;
            return number;
        },
        // 运营效果日同比
        activityGrowthLastDay() {
            let number = this.listState.activityGrowthLastDay;
            this.activityDayUpOrDown = number >= 0 ? true : false;
            return number;
        },
        // 运营效果增长率
        activityRate() {
            return this.listState.activityRate;
        },
    }
}
</script>

